/*! minimal qrcode lib - tiny wrapper around CanvasRenderingContext2D */
// This is a tiny helper using existing browser APIs; for demo only.
window.renderQRCode = function(el, text){
  try {
    if (!el) return;
    // use Google Chart API as a fallback quick render
    const img = document.createElement('img');
    img.src = 'https://chart.googleapis.com/chart?cht=qr&chs=200x200&chl=' + encodeURIComponent(text);
    el.innerHTML = '';
    el.appendChild(img);
  } catch(e){ console.error(e); }
};
